INSERT INTO `wp_posts` VALUES ('1605', '1', '2016-11-20 06:43:35', '2016-11-19 23:43:35', '<p><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Chính chủ cho thuê lớp học tại Hoàng Đạo Thúy giá sốc 50k/h.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Chính chủ cho thuê lớp học tại Hoàng Đạo Thúy giá sốc 50k/h.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Lớp học có vị trí địa lí đắc địa, nằm trung tâm thành phố mặt đường lớn Hoàng Đạo Thúy thuận tiện giao thông, có sân rông 1000m2, bảo vệ trông xe, để ô tô xe máy mái thoải.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Lớp học chất lượng cao có sức chứa đến 15 - 45 người được cung cấp đầy đủ các trang thiết bị hiện đại để phục vụ cho lớp học như: Bàn, ghế, bảng từ trắng, Camera, điều hòa … tất cả đều mới.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Lớp học có không gian giải lao, nghỉ ngơi hơn 100m2 được trang trí phong cách hiện đại, vô cùng thoáng mát, phục vụ trà café tẹt ga.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Giá cho thuê: 12 tr/tháng.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Phí dịch vụ: Điện 3,5k/số, dịch vụ dọn vệ sinh, quản lý chung: 500k/phòng học/tháng.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Gọi ngay cho chị Thúy (chủ nhà): 0903467086.</span><br style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\" /><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">Mail: </span><a style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; text-decoration: none; color: #015f95; font-family: Arial; line-height: 20px;\" href=\"mailto:Newoffice.com.vn@gmail.com\" rel=\"nofollow\">Newoffice.com.vn@gmail.com</a><span style=\"color: #313131; font-family: Arial; font-size: 14px; line-height: 20px;\">.</span></p>', 'Chính chủ cho thuê phòng học Hoàng Đạo Thúy giá sốc 50k/h, 12tr/tháng', '', 'publish', 'open', 'open', '', 'chinh-chu-cho-thue-phong-hoc-hoang-dao-thuy-gia-soc-50kh-12trthang', '', '', '2017-01-29 07:43:35', '2017-01-29 00:43:35', '', '0', 'http://localhost:8080/wordpress/2016/11/20/chinh-chu-cho-thue-phong-hoc-hoang-dao-thuy-gia-soc-50kh-12trthang/', '0', 'post', '', '0');
